package com.folder.boot.controller;

import java.time.ZoneId;
import java.util.ArrayList;
import java.util.Date;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import org.springframework.web.bind.annotation.CrossOrigin;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.PutMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import com.folder.boot.dto.Notice;

@RestController
@CrossOrigin(origins = "*")
@RequestMapping("/Notice")
public class NoticeController {

  @PostMapping("/List")
  public List<?> list() {
    List<Notice> list = new ArrayList<Notice>();
    Notice notice = new Notice();
    notice.setNo(1);
    notice.setTitle("연습용 1");
    notice.setRegDate(new Date().toInstant().atZone(ZoneId.systemDefault()).toLocalDateTime());
    notice.setDesc("스타일리시한 세 가지 케이스 색상. 건강과 피트니스에 대한 통찰을 제공하는 강력한 센서. 혁신적인 안전 기능. 끊김 없이 소통을 유지하는 편리한 방법. 여기에 한층 향상된 성능을 위한 더 빠른 듀얼 코어 프로세서까지. 수많은 기능들로 가득 찬 Apple Watch SE, 그 어느 때보다도 탁월한 가성비를 자랑합니다.");
    list.add(notice);
    return list;
  }

  @PostMapping("/findById")
  public Map<String, Object> findById(@RequestBody Map<String, Object> map) {
    Map<String, Object> resultMap = new HashMap<String, Object>();
    String result = "";
    resultMap.put("content", result);
    resultMap.put("no", Integer.parseInt(map.get("no").toString()));
    resultMap.put("title", "연습용");
    resultMap.put("desc", "Editor.js를 이용한 Notice 화면입니다.");
    resultMap.put("state", true);
    return resultMap;
  }

  @PutMapping("/save")
  public Notice save(@RequestBody Notice notice) {
    System.out.println(notice);
    notice.setNo(1);
    return notice;
  }

}
